query:
  query MeQuery {
    user {
      id
      subscribed_nanodegrees_count
      subscribed_courses_count
      graduated_nanodegrees_count
      graduated_courses_count
      courses(start_index: 0, count: 1, is_graduated: false, order: [last_viewed_at]) {
        key
        title
        summary
        semantic_type
        is_public
        user_state {
          node_key
          last_viewed_at
        }
        lessons {
          key
          title
          semantic_type
          is_public
        }
      }
      nanodegrees(start_index: 0, count: 1, is_graduated: false, include_free_preview: true, order: [last_viewed_at]) {
        key
        title
        summary
        semantic_type
        is_term_based
        is_public
        is_ready_for_graduation
        is_graduated
        packaging_options
        cohorts {
          start_at
          end_at
        }
        enrollment {
          service_model_id
        }
        user_state {
          node_key
          last_viewed_at
        }
        specializations {
          key
          title
          required_count
        }
        parts {
          key
          semantic_type
          is_public
          part_type
          locked_reason
          locked_until
          specialization {
            key
          }
          modules {
            key
            is_public
            lessons {
              key
              is_public
              title
              semantic_type
            }
          }
        }
      }
      graduated_nanodegrees_and_courses: nanodegrees_and_courses(
        start_index: 0, is_graduated: true, order: [last_viewed_at, degrees_first]
      ) {

...on Nanodegree {
  key
  title
  locale
  version
  summary
  semantic_type
  is_term_based
  is_graduated
  is_public
  is_career_oriented
  color_scheme
  packaging_options
  cohorts {
    start_at
    end_at
  }
  enrollment {
    free_preview_status
    high_touch
    started_at
    static_access {
      static_access
    }
  }
}
...on Course {
  key
  title
  locale
  version
  summary
  semantic_type
  is_graduated
  is_graduation_legacy
  is_public
}

      }
      current_nanodegrees_and_courses: nanodegrees_and_courses(
        start_index: 0, is_graduated: false, order: [last_viewed_at, degrees_first], include_free_preview: true
      ) {

...on Nanodegree {
  key
  title
  locale
  version
  summary
  semantic_type
  is_term_based
  is_graduated
  is_public
  is_career_oriented
  color_scheme
  packaging_options
  cohorts {
    start_at
    end_at
  }
  enrollment {
    free_preview_status
    high_touch
    started_at
    static_access {
      static_access
    }
  }
}
...on Course {
  key
  title
  locale
  version
  summary
  semantic_type
  is_graduated
  is_graduation_legacy
  is_public
}

      }
    }
  }
